,STORE_NUMBER
,AUTO_ORDER_OPERATION
,cancel_operation
,note
,person_id
,allprice
,oder_App
,logistics_Id)
values (
#{orderNumber}
,#{orderName}
,sysdate
,#{storeNumber}
,#{autoOrderOperation}
,#{cancelOperation}
,#{note}
,''
,#{allprice}
,#{oderApp}
,#{logisticsId})

mapper.delete:
delete from order_cart
where
store_number=#{storeNumber}
AND material_Number=#{materialNumber}
