mapper.select:
SELECT b.* FROM ( SELECT ROWNUM R, a.* FROM (
select a.material_number, a.stock_count, a.logistics_id, b.material_classification, b.logistics_safety_stock, b.material_name, c.person_id from logistics_stock a join material b on a.material_number = b.material_number join logistics c on a.logistics_id = c.logistics_id where stock_count >= 0 and c.person_id = #{personId} order by a.material_number
) a WHERE ROWNUM <= (#{pageNum} * #{amount}) ) b WHERE R > (#{pageNum}-1) * #{amount}

mapper.update:
		update logistics_order_details
 		set in_operation = #{inOperation}
 		where details_order_number = #{detailsOrderNumber}

update logistics_in
set out_count = #{outCount},
	disposal_operation = #{disposalOperation}
where lot = #{lot}

update logistics_stock
set stock_count = stock_count - #{outCount}
where material_number = #{materialNumber}
and logistics_id = #{logisticsId}

mapper.insert:
{
	call logistics_out_p(
		#{pNumber, mode=IN, jdbcType=VARCHAR},
		#{pOutOperation, mode=IN, jdbcType=VARCHAR},
		#{pInspection, mode=IN, jdbcType=VARCHAR},
		#{result, mode=OUT, jdbcType=INTEGER}
	)
}
