mapper.insert:

SELECT 'CTN' || LPAD((MAX(SUBSTR(logistics_id, 4))+1), '7', '0')
FROM logistics

INSERT INTO logistics (logistics_id
                      , logistics_name
                      , postal
                      , basics_address
                      , details_address
                      , note
                      , phone
                      , permission_day
                      , person_id)
VALUES ( #{logisticsId}
        , #{logisticsName}
        , #{postal}
        , #{basicsAddress}
        , #{detailsAddress}
        , #{note}
        , #{phone}
        , #{permissionDay}
        , #{personId})

mapper.update:
UPDATE logistics
SET logistics_name = #{logisticsName}
    , basics_address = #{basicsAddress}
    , details_address = #{detailsAddress}
    , note = #{note}
    , postal = #{postal}
    , phone = #{phone}
WHERE logistics_id=#{logisticsId}
